update.foreach:
update orderdetail
DetailAddress=#{item.detailaddress}, GoodsUsingTime=#{item.goodsusingtime}, Name=#{item.name}, Phone=#{item.phone}, GoodsRemark=#{item.goodsremark},
where WXCode = #{item.wxcode}

mapper.update:
update orderdetail
DetailAddress= #{detailaddress,jdbcType=VARCHAR}, OrderState = 3, GoodsUsingTime= #{goodsusingtime,jdbcType=TIMESTAMP}, Name= #{name,jdbcType=VARCHAR}, Phone= #{phone,jdbcType=VARCHAR}, GoodsRemark= #{goodsremark,jdbcType=VARCHAR}
where WXCode = #{wxcode,jdbcType=VARCHAR}
